mapper.select:
SELECT rw.*, user.realName realName FROM rw LEFT JOIN USER ON rw.rwZrId = user.id
bwFlag = 1
and rwTitle like "%"#{rwTitle,jdbcType=VARCHAR}"%"
and rwZrId = #{rwZrId}
and state = #{state}
and cq = #{cq}
ORDER BY pubTime DESC

SELECT rw.*, user.realName realName FROM rw LEFT JOIN USER ON rw.rwZrId = user.id
bwFlag = 0
and rwTitle like "%"#{rwTitle,jdbcType=VARCHAR}"%"
and rwZrId = #{rwZrId}
and state = #{state}
and cq = #{cq}
ORDER BY pubTime DESC

SELECT rw.*, user.realName realName FROM rw LEFT JOIN USER ON rw.rwZrId = user.id WHERE rw.rwFpId = #{rwFpId} and bwFlag
and rwTitle like "%"#{rwTitle,jdbcType=VARCHAR}"%"
and rwZrId = #{rwZrId}
and state = #{state}
and cq = #{cq}
ORDER BY pubTime DESC

SELECT rw.*, user.realName realName FROM rw LEFT JOIN USER ON rw.rwZrId = user.id WHERE rw.rwZrId = #{rwZrId} and bwFlag
and rwTitle like "%"#{rwTitle,jdbcType=VARCHAR}"%"
and state = #{state}
and cq = #{cq}
ORDER BY pubTime DESC

SELECT rw.*, user.realName realName FROM rw LEFT JOIN USER ON rw.rwZrId = user.id WHERE rw.id IN ( SELECT rwId FROM rw_fp WHERE receiveId = #{rwCyId} ) and bwFlag = 1
and rwTitle like "%"#{rwTitle,jdbcType=VARCHAR}"%"
and rwZrId = #{rwZrId}
and state = #{state}
and cq = #{cq}
ORDER BY pubTime DESC
