mapper.select:
SELECT rw.*, user.realName realName FROM rw LEFT JOIN USER ON rw.rwZrId = user.id
rwTitle like "%"#{rwTitle,jdbcType=VARCHAR}"%"
and rwZrId = #{rwZrId}
and state = #{state}
and cq = #{cq}
ORDER BY pubTime DESC

SELECT rw.*, user.realName realName, category.categoryName FROM rw LEFT JOIN USER ON rw.rwZrId = user.id LEFT JOIN category ON category.id = rw.rwCategoryId WHERE rw.rwFpId = #{rwFpId}
and rwTitle like "%"#{rwTitle,jdbcType=VARCHAR}"%"
and rwZrId = #{rwZrId}
and state = #{state}
and cq = #{cq}
ORDER BY pubTime DESC

SELECT rw.*, user.realName realName, category.categoryName FROM rw LEFT JOIN USER ON rw.rwZrId = user.id LEFT JOIN category ON category.id = rw.rwCategoryId WHERE rw.rwZrId = #{rwZrId}
and rwTitle like "%"#{rwTitle,jdbcType=VARCHAR}"%"
and state = #{state}
and cq = #{cq}
ORDER BY pubTime DESC

SELECT rw.*, user.realName realName, category.categoryName FROM rw LEFT JOIN USER ON rw.rwZrId = user.id LEFT JOIN category ON category.id = rw.rwCategoryId WHERE rw.id IN ( SELECT rwId FROM rw_fp WHERE receiveId = #{rwCyId} )
and rwTitle like "%"#{rwTitle,jdbcType=VARCHAR}"%"
and rwZrId = #{rwZrId}
and state = #{state}
and cq = #{cq}
ORDER BY pubTime DESC
